import { useParams } from 'react-router-dom';
import { Container, Row, Col, Button } from 'react-bootstrap';

export default function Room() {
  const { roomId } = useParams();

  return (
    <Container fluid className="p-4">
      <h3>Sala: {roomId}</h3>
      <Row className="mt-4">
        <Col md={6} className="bg-dark text-white text-center p-4 rounded">
          Video Local (Tu cámara)
        </Col>
        <Col md={6} className="bg-secondary text-white text-center p-4 rounded">
          Video Remoto (Otro participante)
        </Col>
      </Row>
      <div className="text-center mt-4">
        <Button variant="danger" className="mx-2">Colgar</Button>
        <Button variant="success" className="mx-2">Cámara</Button>
        <Button variant="info" className="mx-2">Micrófono</Button>
      </div>
    </Container>
  );
}
