import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Button, Form, Row, Col, Card } from 'react-bootstrap';

export default function Home() {
  const [roomId, setRoomId] = useState('');
  const navigate = useNavigate();

  const joinRoom = () => {
    if (roomId.trim()) navigate(`/room/${roomId}`);
  };

  const createNewMeeting = () => {
    const newId = Math.random().toString(36).substring(2, 10);
    navigate(`/room/${newId}`);
  };

  return (
    <Container className="d-flex align-items-center justify-content-center min-vh-100">
      <Card className="p-4 shadow-lg" style={{ width: '100%', maxWidth: 400 }}>
        <Card.Body>
          <Card.Title className="mb-4 text-center">
            <h2>SamirMeet</h2>
            <p className="text-muted">Reuniones seguras y sin interrupciones</p>
          </Card.Title>

          <Button variant="primary" className="w-100 mb-3" onClick={createNewMeeting}>
            Nueva reunión
          </Button>

          <Form.Group controlId="formRoomId" className="mb-2">
            <Form.Control
              type="text"
              placeholder="Ingresa un código de reunión"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
            />
          </Form.Group>
          <Button variant="outline-primary" className="w-100" onClick={joinRoom}>
            Unirse
          </Button>
        </Card.Body>
      </Card>
    </Container>
  );
}
